import { Audio } from "expo-av";


const soundObject = new Audio.Sound();
export async function playSound(soundName) {
  
  await soundObject.unloadAsync();
  switch (soundName) {
    case "bark":
      try {
        await soundObject.loadAsync(require("../../assets/bark.mp3"));
        await soundObject.playAsync();
        // Your sound is playing!
      } catch (error) {
        // An error occurred!
        await soundObject.playAsync();
      }
      break;

    case "meow":
      try {
        await soundObject.loadAsync(require("../../assets/meow.mp3"));
        await soundObject.playAsync();
        // Your sound is playing!
      } catch (error) {
        // An error occurred!
        await soundObject.playAsync();
      }
      break;

      case "bleat":
      try {
        await soundObject.loadAsync(require("../../assets/bleat.mp3"));
        await soundObject.playAsync();
        // Your sound is playing!
      } catch (error) {
        // An error occurred!
        await soundObject.playAsync();
      }
      break;

    default:
      break;
  }
}
