import React from "react";
import { View, Text, StyleSheet, Alert } from "react-native";
import { playSound } from "../apis/audio_api";
import SortableGridView from "react-native-sortable-gridview";

function HomeScreen() {
  return (
    // <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
    //   <Button title="Bark" onPress={() => playSound("bark")}/>
    //   <Button title="Meow" onPress={() => playSound("meow")}/>
    //   <Button title="Bleat" onPress={() => playSound("bleat")}/>
    //   <Button title="Hey" onPress={() => playSound()}/>
    // </View>

    <SortableGridView
      data={[
        { name: "Bark", backgroundColor: "#09f", color: "#fff" },
        { name: "Meow", backgroundColor: "#f60", color: "#fff" },
        { name: "Bleat", backgroundColor: "#333", color: "#fff" },
        {
          name: "Hey",
          backgroundColor: "#rgba(255, 216, 58, 1)",
          color: "#333",
        },
        {
          name: "Other Sound",
          backgroundColor: "#rgba(0, 222, 144, 1)",
          color: "#fff",
        },
      ]}
      onDragStart={() => {
        console.log("Default onDragStart");
      }}
      onDragRelease={(data) => {
        console.log("Default onDragRelease", data);
      }}
      renderItem={(item, index) => {
        return (
          <View
            uniqueKey={item.name} // Important! Should add this props!!!
            onTap={() => playSound("bleat")}
            style={[styles.item, { backgroundColor: item.backgroundColor }]}
          >
            <Text style={[styles.text, { color: item.color }]}>
              {item.name}
            </Text>
          </View>
        );
      }}
    />
  );
}

const styles = StyleSheet.create({
  item: {
    flex: 1,
    borderRadius: 4,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default HomeScreen;
