import React from "react";
import {View, Text, StyleSheet, Button} from "react-native";
import { Audio } from 'expo-av';

function HomeScreen() {
    
    

    async function playSound(soundName="bark"){
      switch (soundName) {
        case "bark":
          try {
            const soundObject = new Audio.Sound();
            await soundObject.loadAsync(require('../../assets/bark.mp3'));
            await soundObject.playAsync();
            // Your sound is playing!
        } catch (error) {
            // An error occurred!
            await soundObject.playAsync();
        }
          break;

        case "meow":
          try {
            const soundObject = new Audio.Sound();
            await soundObject.loadAsync(require('../../assets/meow.mp3'));
            await soundObject.playAsync();
            // Your sound is playing!
        } catch (error) {
            // An error occurred!
            await soundObject.playAsync();
        }
          break;
      
        default:
          break;
      }
        
    }

    return (
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Button title="Bark" onPress={() => playSound("bark")}/>
        <Button title="Meow" onPress={() => playSound("meow")}/>
        <Button title="Hey" onPress={() => playSound()}/>
        <Button title="Hey" onPress={() => playSound()}/>
      </View>
    );
  }

const styles = StyleSheet.create({

});

export default HomeScreen;
